import React from "react";
import { GridComponent, Inject, ColumnsDirective, ColumnDirective, Search, Page } from "@syncfusion/ej2-react-grids";

import { employeesData, employeesGrid } from "../data/dummy";
import { Header } from "../components";

const Employees = () => {
  const toolbarOptions = ["Search"];

  const editing = { allowDeleting: true, allowEditing: true };

  return (
    <div
      className="m-2 md:m-10 mt-24 p-2 md:p-10 bg-white rounded-3x1"
      style={{
        backgroundImage: "url(your-image-url)",
        backgroundSize: "cover",
        width: "90%",
        height: "100vh", // You can adjust the height as needed
      }}>
      <Header category="Page" title="Employees" />
      <GridComponent dataSource={employeesData} width="100%" allowPaging allowSorting pageSettings={{ pageCount: 5 }} editSettings={editing} toolbar={toolbarOptions}>
        <ColumnsDirective>
          {/* eslint-disable-next-line react/jsx-props-no-spreading */}
          {employeesGrid.map((item, index) => (
            <ColumnDirective key={index} {...item} />
          ))}
        </ColumnsDirective>
        <Inject services={[Search, Page]} />
      </GridComponent>
    </div>
  );
};
export default Employees;
